// src/components/Content.js
import React, { useState } from "react";
import Checkbox from "./Checkbox";
import Button from "./Button";
import EditButton from "./EditButton";

const Content = () => {
  const [todoList, setTodoList] = useState([
    { id: 1, title: "Learn React", completed: false },
    { id: 2, title: "Learn AI", completed: true },
    { id: 3, title: "Learn Angular", completed: false },
    { id: 4, title: "Learn Node", completed: true },
  ]);

  const [editId, setEditId] = useState(null);
  const [editText, setEditText] = useState("");

  const handleToggleComplete = (id) => {
    setTodoList(
      todoList.map((todo) =>
        todo.id === id ? { ...todo, completed: !todo.completed } : todo
      )
    );
  };

  const handleDelete = (id) => {
    setTodoList(todoList.filter((todo) => todo.id !== id));
  };

  const handleSave = (id) => {
    setTodoList(
      todoList.map((todo) =>
        todo.id === id ? { ...todo, title: editText } : todo
      )
    );
    setEditId(null);
    setEditText("");
  };

  return (
    <main>
      <ul>
        {todoList.map((todo) => (
          <li className="item" key={todo.id} style={{ display: "flex", alignItems: "center", gap: "10px" }}>
            <Checkbox
              checked={todo.completed}
              onChange={() => handleToggleComplete(todo.id)}
            />

            {editId === todo.id ? (
              <input
                type="text"
                value={editText}
                onChange={(e) => setEditText(e.target.value)}
                onBlur={() => handleSave(todo.id)}
                autoFocus
              />
            ) : (
              <label>{todo.title}</label>
            )}

            <EditButton
              onClick={() => {
                setEditId(todo.id);
                setEditText(todo.title);
              }}
            />
            <Button onClick={() => handleDelete(todo.id)} />
          </li>
        ))}
      </ul>
    </main>
  );
};

export default Content;
